function getDate() {
    let date = new Date();
    let year = date.getYear() + 1900;
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let month = months[date.getMonth()];
    let day = date.getDate().toString().length < 2 ? "0" + date.getDate() : date.getDate();
    let hour = date.getHours().toString().length < 2 ? "0" + date.getHours() : date.getHours();
    let minute = date.getMinutes().toString().length < 2 ? "0" + date.getMinutes() : date.getMinutes();
    let second = date.getSeconds().toString().length < 2 ? "0" + date.getSeconds() : date.getSeconds();
    return year + "-" + month + "-" + day + " " + hour + ":" + minute + ":" + second;
}
// Laden der Events
fs.readdir("./events/", (err, files) => {
    if (err) return console.error;
    files.forEach((file) => {
        if (!file.endsWith(".js")) return;
        const evt = require(`./events/${file}`);
        let evtName = file.split(".")[0];
        console.log(`${getDate()} | Loaded Event '${evtName}'`);
        client.on(evtName, evt.bind(null, client));
    });
});

//Laden aller Commands
fs.readdir("./commands/", async (err, files) => {
    files.forEach((file) => {
        if (!file.endsWith(".js")) return;
        let props = require(`./commands/${file}`);
        let cmdName = file.split(".")[0];
        console.log(`${getDate()} | Loaded Command '${cmdName}'`);
        client.commands.set(cmdName, props);
    });
});

fs.readdir("./commands/authorised/", async (err, files) => {
    files.forEach((file) => {
        if (!file.endsWith(".js")) return;
        let props = require(`./commands/authorised/${file}`);
        let cmdName = file.split(".")[0];
        console.log(`${getDate()} | Loaded Command '${cmdName}'`);
        client.commands.set(cmdName, props);
    });
});

process.on("unhandledRejection", (error) => {
    console.error(getDate() + " | Unhandled promise rejection:", error);
});

client.login(config.token || process.env.TOKEN);